mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT *
FROM (
	SELECT A.*, ROWNUM AS rnum
	FROM (
		SELECT m.*, a.AUTHORITIES_AUTHORITY
		FROM MEMBER m
		LEFT JOIN AUTHORITIES a ON m.MEMBER_ID = a.MEMBER_ID
		ORDER BY m.MEMBER_CREATE DESC
		
	) A
	
	WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}
