mapper.select:
	SELECT *
	FROM (
		SELECT A.*, ROWNUM AS rnum
		FROM (
			SELECT *
			FROM PRODUCT



                	ORDER BY PRODUCT_PRICE DESC


                	ORDER BY PRODUCT_PRICE ASC


                	ORDER BY PRODUCT_ID DESC


		) A

		WHERE ROWNUM <= #{endRow}
	)
	WHERE rnum >= #{startRow}
